parameters.jsonBody:
={
  "query": "query GetTransactions($after: String) { transactions(first: 50, after: $after) { pageInfo { hasNextPage endCursor } edges { node { ... on Payment { id amount transactionDate category source description transactedFor { ... on Invoice { id visualId } } timestamps { createdAt } } } } } }",
  "variables": { "after": {{ $json.cursor ? '"' + $json.cursor + '"' : 'null' }} }
}

parameters.jsCode:
// Process transactions and prepare for next page
// Printavo transactions use union type - we query ... on Payment {}
// Payment.transactedFor.Invoice links to the order
const response = $input.first().json;
const pageInfo = response.data?.transactions?.pageInfo || {};
const edges = response.data?.transactions?.edges || [];

// Get previous accumulated transactions
const prevData = $('Has More Pages?').first().json;
const allTransactions = prevData.allTransactions || [];

// Add new transactions to accumulated list
// Filter to only include payments with linked invoices (orders)
const newTransactions = edges
  .filter(edge => edge.node && edge.node.id && edge.node.transactedFor?.id)
  .map(edge => {
    const payment = edge.node;
    const invoice = payment.transactedFor || {};
    const timestamps = payment.timestamps || {};

    return {
      printavo_id: payment.id,
      amount: parseFloat(payment.amount) || 0,
      payment_date: payment.transactionDate,
      payment_method: payment.source || payment.category || 'Unknown',
      notes: payment.description || payment.category || '',
      order_printavo_id: invoice.id,
      order_visual_id: invoice.visualId,
      created_at: timestamps.createdAt
    };
  });

allTransactions.push(...newTransactions);

return [{
  json: {
    cursor: pageInfo.endCursor || null,
    hasMore: pageInfo.hasNextPage || false,
    allTransactions: allTransactions,
    pageCount: (prevData.pageCount || 0) + 1
  }
}];

// Output all accumulated transactions for database upsert
const data = $input.first().json;
const transactions = data.allTransactions || [];

console.log(`Total transactions fetched: ${transactions.length}`);

// Return each transaction as a separate item
return transactions.map(t => ({ json: t }));

// Summary of sync results
const items = $input.all();
const totalProcessed = items.length;

return [{
  json: {
    workflow: 'Printavo Transaction Sync',
    timestamp: new Date().toISOString(),
    transactionsProcessed: totalProcessed,
    status: 'completed'
  }
}];
