={
  "query": "query GetTasks($after: String) { tasks(first: 50, after: $after) { pageInfo { hasNextPage endCursor } edges { node { id name dueAt completedAt taskable { ... on Invoice { id visualId } ... on Quote { id visualId } } } } } }",
  "variables": { "after": {{ $json.cursor ? '"' + $json.cursor + '"' : 'null' }} }
}

parameters.jsCode:
// Process tasks
const response = $input.first().json;
const pageInfo = response.data?.tasks?.pageInfo || {};
const edges = response.data?.tasks?.edges || [];

const prevData = $('Has More Pages?').first().json;
const allTasks = prevData.allTasks || [];

const newTasks = edges.map(edge => {
  const node = edge.node;
  const taskable = node.taskable || {};
  return {
    printavo_id: node.id,
    name: node.name || '',
    due_date: node.dueAt,
    completed_at: node.completedAt,
    order_printavo_id: taskable.id || null,
    order_visual_id: taskable.visualId || null
  };
});

allTasks.push(...newTasks);

console.log(`Tasks page ${prevData.pageCount || 1}: fetched ${newTasks.length}`);

return [{
  json: {
    cursor: pageInfo.endCursor || null,
    hasMore: pageInfo.hasNextPage || false,
    allTasks: allTasks,
    pageCount: (prevData.pageCount || 0) + 1
  }
}];

// Output all tasks for database upsert
const data = $input.first().json;
const tasks = data.allTasks || [];

console.log(`Total tasks fetched: ${tasks.length}`);

return tasks.map(task => ({ json: task }));

// Summary
const items = $input.all();
return [{
  json: {
    workflow: 'Printavo Task Sync',
    timestamp: new Date().toISOString(),
    tasksSynced: items.length,
    status: 'completed'
  }
}];
