parameters.jsonBody:
={
  "query": "query GetInvoices($after: String) { invoices(first: 50, after: $after) { pageInfo { hasNextPage endCursor } edges { node { id visualId nickname status { name } contact { id firstName lastName email phone customer { id companyName } } total salesTaxAmount discount productionNote customerNote dueAt timestamps { createdAt updatedAt } } } } }",
  "variables": { "after": {{ $json.cursor ? '"' + $json.cursor + '"' : 'null' }} }
}

parameters.jsCode:
// Process invoices and prepare for next page
// Printavo uses 'invoices' not 'orders', and 'contact' for customer info
const response = $input.first().json;
const pageInfo = response.data?.invoices?.pageInfo || {};
const edges = response.data?.invoices?.edges || [];

// Get previous accumulated orders
const prevData = $('Has More Pages?').first().json;
const allOrders = prevData.allOrders || [];

// Add new orders to accumulated list
const newOrders = edges.map(edge => {
  const node = edge.node;
  const contact = node.contact || {};
  const customer = contact.customer || {};
  const timestamps = node.timestamps || {};

  return {
    // Order fields
    printavo_id: node.id,
    visual_id: node.visualId,
    nickname: node.nickname || '',
    status: node.status?.name || 'Unknown',
    total_amount: parseFloat(node.total) || 0,
    tax_amount: parseFloat(node.salesTaxAmount) || 0,
    discount_amount: parseFloat(node.discount) || 0,
    production_notes: node.productionNote || '',
    customer_note: node.customerNote || '',
    due_date: node.dueAt,
    created_at: timestamps.createdAt,
    updated_at: timestamps.updatedAt,
    // Customer data - from contact and contact.customer
    customer_printavo_id: customer.id || contact.id,
    customer_first_name: contact.firstName || '',
    customer_last_name: contact.lastName || '',
    customer_email: contact.email || '',
    customer_phone: contact.phone || '',
    customer_company: customer.companyName || ''
  };
});

allOrders.push(...newOrders);

return [{
  json: {
    cursor: pageInfo.endCursor || null,
    hasMore: pageInfo.hasNextPage || false,
    allOrders: allOrders,
    pageCount: (prevData.pageCount || 0) + 1
  }
}];

// Output all accumulated orders for database upsert
const data = $input.first().json;
const orders = data.allOrders || [];

console.log(`Total invoices fetched: ${orders.length}`);

// Return each order as a separate item
return orders.map(order => ({ json: order }));

// Summary of sync results
const items = $input.all();
const totalProcessed = items.length;

return [{
  json: {
    workflow: 'Printavo Daily Order Sync',
    timestamp: new Date().toISOString(),
    ordersProcessed: totalProcessed,
    status: 'completed'
  }
}];
